/* * Click nbfs://nbhost/SystemFileSystem/Templates/Licenses/license-default.txt to change this license * Click nbfs://nbhost/SystemFileSystem/Templates/Classes/Class.java to edit this template */package cau1;import java.util.Locale;import java.util.Scanner;import java.text.NumberFormat;public class Account {    private double soTien = 50;    private long stk = 999999;    private String tenTK = null;    private String trangThai;    final double laiSuat =  0.035;    Locale local = new Locale ("vi", "vn");    NumberFormat formatter = NumberFormat.getCurrencyInstance(local);    public Account(double soTien, long stk, String tenTK) {        this.soTien = soTien;        this.stk = stk;        this.tenTK = tenTK;    }        public Account(int stk, String tenTK){        this.stk = stk;        this.tenTK = tenTK;    }    public Account(){            }    public double getSoTien() {        return soTien;    }    public void setSoTien(double soTien) {        if ( this.soTien >= 50){             this.soTien = soTien;           }else{            System.out.println(this.trangThai);        }    }    public long getStk() {        return stk;    }    public void setStk(int stk) {        if (this.stk > 0 && this.stk != 999999){            this.stk = stk;        }else {            System.out.println(this.trangThai);        }    }    public String getTenTK() {        return tenTK;    }    public void setTenTK(String tenTK) {        if (this.tenTK != null){            this.tenTK = tenTK;        }else{            System.out.println(this.trangThai);;        }    }    public String getTrangThai() {        return trangThai;    }    public void setTrangThai(String trangThai) {        this.trangThai = trangThai;    }        public void napTien(){        Scanner sc = new Scanner (System.in);        soTien = sc.nextDouble();        if (soTien < 0){            System.out.println("Nap tien khong hop le!!!!");        }        this.soTien += soTien;    }    public void rutTien(){        Scanner sc = new Scanner (System.in);        System.out.println("Nhap so tien can rut:");        soTien = sc.nextDouble();        if (soTien > this.soTien){            System.out.println("Rut tien khong hop le!!!!");        }        this.soTien -= soTien;    }    public void chuyenKhoan(Account account, double soTien){        Scanner sc = new Scanner (System.in);        soTien = sc.nextDouble();        if ( soTien >0 && soTien <this.soTien){            this.soTien -= soTien;            account.soTien += soTien;        }else{            System.out.println("Chuyen khoan khong hop le!!!");        }    }    public void daoHan(){        this.soTien = this.soTien + this.soTien * laiSuat;    }    public void nhap(){        Scanner sc = new Scanner (System.in);        System.out.println("Nhap so TK: ");        stk = sc.nextLong();        System.out.println("Nhap ten TK:");        sc.nextLine();        tenTK = sc.nextLine();        System.out.println("Nhap so tien:");        soTien = sc.nextDouble();            }    @Override    public String toString() {        return "Account{" + "soTien=" + formatter.format(soTien) + " "+                ", stk=" + getStk() + " "+                ", tenTK=" + getTenTK() + " "+                ", trangThai=" + getTrangThai() + '}';    }    }